def get_key(array, key, value):
	for item in array:
		if item[key] == value:
			return item


def choice_city():
	print('Популярные города' + '\n')
	for city in cities:
		if city['popular']:
			print(str(city['label']) + ': [' + str(city['id']) + ']' )
	city_id = get_integer('Пожалуйста, введите код города', default = 0)
	print('Выбран город: ' + get_key(cities, 'id', city_id)['label'])
	return city_id
	
def get_integer(message, name="integer", default=None, minimum=0,
                maximum=1000, allow_zero=True):

    class RangeError(Exception): pass

    message += ": " if default is None else " [{0}]: ".format(default)
    while True:
        try:
            line = input(message)
            if not line and default is not None:
                return default
            i = int(line)
            if i == 0:
                if allow_zero:
                    return i
                else:
                    raise RangeError("{0} may not be 0".format(name))
            if not (minimum <= i <= maximum):
                raise RangeError("{0} must be between {1} and {2} "
                        "inclusive{3}".format(name, minimum, maximum,
                        (" (or 0)" if allow_zero else "")))
            return i
        except RangeError as err:
            print("ERROR", err)
        except ValueError as err:
            print("ERROR {0} must be an integer".format(name))

